func Post(post_data url.Values) []byte {

	//data1 := url.Values{"start": {"0"}, "offset": {"xxxx"}}
	body := strings.NewReader(post_data.Encode())

	req, err := http.NewRequest("POST", Url_string, body)
	if err != nil {
		log.Fatal(err)
	}
	req.Header.Set("Content-Type", Content_type)
	req.Header.Set("user-agent", User_agent)
	req.Header.Add("Origin", Origin)
	req.Header.Add("Referer", Referer)
	clt := http.Client{}
	response, err := clt.Do(req)
	if err != nil {
		log.Fatal(err)
	}
	defer response.Body.Close()
	body2, err := ioutil.ReadAll(response.Body)
	if err != nil {
		log.Fatal(err)
	}
	//fmt.Printf("%s", body2)
	fmt.Println("read html success")
	return body2
}

//convert BIG5 to UTF-8
func DecodeBig5(s []byte) ([]byte, error) {
	I := bytes.NewReader(s)
	O := transform.NewReader(I, traditionalchinese.Big5.NewDecoder())
	d, e := ioutil.ReadAll(O)
	if e != nil {
		return nil, e
	}
	return d, nil
}

func Twse_Get_CVS(url_string string) ([]byte, string) {
	data := url.Values{}
	body := strings.NewReader(data.Encode())
	req, err := http.NewRequest("GET", url_string, body)
	if err != nil {
		log.Fatal(err)
	}
	req.Header.Set("Content-Type", Content_type)
	req.Header.Set("user-agent", User_agent)
	req.Header.Add("Referer", Referer)
	clt := http.Client{}
	response, err := clt.Do(req)
	if err != nil {
		log.Fatal(err)
	}
	defer response.Body.Close()
	contentDisposition := response.Header.Get("Content-Disposition")
	disposition, params, err := mime.ParseMediaType(contentDisposition)
	filename := params["filename"] // set to "foo.png"
	fmt.Println(disposition)

	body2, err := ioutil.ReadAll(response.Body)
	if err != nil {
		log.Fatal(err)
	}
	//fmt.Printf("%s", body2)
	fmt.Println("read html success")
	//body3 := string(DecodeBig5(body2))
	// BIG5 转 UTF-8
	s, _ := DecodeBig5(body2)
	//body3 := string(s)
	return s, filename
}

